    private void jButtonBusquedaBActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonBusquedaBActionPerformed
        int huespedBusq = Integer.parseInt(JOptionPane.showInputDialog("Ingrese el Codigo a buscar :"));

        refE.ordenarEmpleadoPorCODIGO();

        if (refE.busquedaB(huespedBusq) != null) {
            JOptionPane.showMessageDialog(null, refE.busquedaB(huespedBusq).getNombre()
                    + "  " + refE.busquedaB(huespedBusq).getApellido());

        } else {
            JOptionPane.showMessageDialog(null, "Empleado no existe .....");
        }
    }//GEN-LAST:event_jButtonBusquedaBActionPerformed

    private void btnGuardarEActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarEActionPerformed
        int cod = Integer.parseInt(txtCodigoE.getText());
        String nomb = txtNombreE.getText();
        String Apell = txtApellidoE.getText();
        int dniH = Integer.parseInt(txtFRH.getText());

        Empleado refC = new Empleado(cod, nomb, Apell, dniH);
        refE.ingresarEmpleado(refC);
        listarEmpleado();

        //Limpiamos controles
        txtCodigoE.setText("");
        txtNombreE.setText("");
        txtApellidoE.setText("");
        txtFRH.setText("");
    }//GEN-LAST:event_btnGuardarEActionPerformed

