    private void listarEmpleado() {
        for (int i = 0; i < refE.getListaE().size(); i++) {
            tablaEMPLEADO.setValueAt(refE.getListaE().get(i).getCodigo(), i, 0);
            tablaEMPLEADO.setValueAt(refE.getListaE().get(i).getNombre(), i, 1);
            tablaEMPLEADO.setValueAt(refE.getListaE().get(i).getApellido(), i, 2);
            tablaEMPLEADO.setValueAt(refE.getListaE().get(i).getDniHuesped(), i, 3);
        }
    }


